,p_script_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'create or replace package gfm_builder',
'',
'-- Build a new Form page in an existing Oracle APEX Application',
'-- GPM Factory',
'-- v1.2 sep 2022',
'-- v1.0 oct 2021',
'-- P. Monaco',
'',
'as',
'function generate_form(fid number, pid number DEFAULT null)',
'return number;',
'',
'procedure generate_ddl (fid number, ptype varchar2 default ''M'');',
'',
'function get_lov (id number) ',
'return varchar2;',
'function get_lov (plov varchar2) ',
'return varchar2;',
'',
'function get_lov_master (id varchar2) ',
'return varchar2;',
'',
'function getNewPageid (fid number)',
'return number;',
'',
'function copyForm(fid number, appid number)',
'return number;',
'',
'function copyItems(fid number       --origin form',
'                    , tid number    -- target form',
'                  )',
'return number;',
'',
'function reseq (pid number, plist varchar2)',
'return number;',
'',
'-- Bootstrap preferences for a given form',
'function init_prefs(fid number)    -- form id',
'return number;',
'',
'-- reset form and data',
'function reset_form(fid number)    -- form id',
'return number;',
'',
'end;',
'/',
'create or replace package body gfm_builder',
'',
'-- Build a new Form page in an existing Oracle APEX Application',
'-- GPM Factory',
'-- v1.2 sep 2022',
'-- v1.1 jan 2022',
'-- P. Monaco',
'',
'as',
'',
'function to_boolean',
'  ( p_string varchar2',
'  ) return boolean',
'is',
'begin',
'  return',
'    case upper(p_string) ',
'      when ''TRUE'' then true',
'      when ''FALSE'' then false',
'      else null',
'      end;',
'end;',
'',
'-- Get a parameter value',
'function getParam(pname VARCHAR2) ',
'return VARCHAR2 ',
'is ',
'tval VARCHAR2(200); ',
' ',
'begin ',
'    select valeur into tval ',
'    from GFM_SETUP_V',
'    where parameter = pname; ',
'    return tval; ',
'exception ',
'    when others then ',
'    return ''NOK''; ',
'end; ',
'',
'',
'procedure bootstrap',
'',
'-- Not used anymore',
'is',
'begin',
'/*',
'--select * from apex_application_pages where user_interface_id = 13238501903945206',
'',
'select distinct user_interface_id into l_user_interface_id from apex_application_pages',
'where application_id = l_flow_id; ',
'',
'select template_id into l_plug_template from apex_application_templates',
'where template_type = ''Region'' and internal_name = ''STANDARD'' and application_id = l_flow_id;',
'select template_id into  l_plug_template_tabs from apex_application_templates',
'where template_type = ''Region'' and internal_name = ''TABS_CONTAINER'' and application_id = l_flow_id;',
'',
'select template_id into l_button_template_id from apex_application_templates',
'where template_type = ''Button'' and internal_name = ''TEXT'' and application_id = l_flow_id;',
'',
'select template_id into l_field_template from apex_application_templates',
'where template_type = ''Item Label'' and internal_name = ''OPTIONAL_FLOATING'' and application_id = l_flow_id;',
'',
' l_user_interface_id    NUMBER := 13238501903945206;      -- Page STANDARD --13238501903945206;',
' l_plug_template        NUMBER := 13148911441945113;    -- Region	STANDARD',
' l_plug_template_tabs   NUMBER := 13155707099945116;    -- Region	TABS_CONTAINER',
' l_button_template_id   NUMBER := 13214017098945164;    -- Button	TEXT',
' l_field_template       NUMBER := 13211557155945161;    -- ''Item Label''	OPTIONAL_FLOATING',
'*/',
'null;',
'end;',
'',
'-- -------------------------------------------------------------',
'-- MAIN MODULE',
'-- -------------------------------------------------------------',
'',
'function generate_form(fid number                       -- form id',
'                        , pid number DEFAULT null   )   -- page id',
'return number',
'is',
' l_static_region_id   NUMBER;',
' l_region_id          NUMBER;',
' l_form_region_id     NUMBER;',
' l_form_container_id  NUMBER;',
' l_subform_id         NUMBER;',
' type subforms_id IS VARRAY(10) OF NUMBER;',
' TYPE subforms_array_type ',
'    IS TABLE OF NUMBER ',
'    INDEX BY VARCHAR2(100);',
' subforms  subforms_array_type;  ',
'',
' TYPE template_array_type ',
'    IS TABLE OF NUMBER ',
'    INDEX BY VARCHAR2(100);',
' itemplates  template_array_type; ',
'',
'TYPE prefs_array_type ',
'    IS TABLE OF VARCHAR2(100) ',
'    INDEX BY VARCHAR2(100);',
'prefs prefs_array_type;',
'',
' type items_array_type IS VARRAY(100) OF NUMBER;',
' items_arr   items_array_type := items_array_type(); ',
' r_prefs GFM_FORM_PREFS%ROWTYPE;',
' pref_item_template VARCHAR2(100);',
'',
' -- Template id',
' l_user_interface_id    NUMBER; -- := 13238501903945206;    -- Page STANDARD ',
' l_plug_template        NUMBER; -- := 13148911441945113;    -- Region STANDARD',
' l_plug_template_tabs   NUMBER; -- := 13155707099945116;    -- Region TABS_CONTAINER',
' l_button_template_id   NUMBER; -- := 13214017098945164;    -- Button TEXT',
' l_field_template       NUMBER; -- := 13211557155945161;    -- Item Label	OPTIONAL_FLOATING',
' l_field_optional       NUMBER; -- Item Label OPTIONAL',
' l_plug_template_std    NUMBER; -- Main Region',
' l_step_template_id     NUMBER; -- used for page template',
'',
' lov_array  apex_t_varchar2;',
' ',
' test       varchar2(10);',
' l_flow_id  NUMBER;           ',
' l_page_id  NUMBER;',
' step       varchar2(50);',
' l_id       NUMBER;',
' tseq       NUMBER := 0;',
' form_rec   GFM_FORM%ROWTYPE;',
' tdisplay   varchar2(100);',
' attr1      varchar2(100);',
' attr2      varchar2(100);',
' attr3      varchar2(100);',
' nbsub      number := 0;        -- subregions counter',
' subregion_id number;',
' tsections  varchar2(1000);',
' titems     varchar2(1000);',
' tmsg       varchar2(1000);',
' ttemplate  number;',
' tlist      varchar2(4000);',
' delete_id  number;',
' ig_region_id number;',
' ig_id      number;',
' report_id  number;',
' view_id    number;',
' static_id  varchar2(10);',
' tid        number;',
' i          number;',
' vreport_id number;             -- report id get from view ',
' vview_id   number;             -- view id get from view ',
' tauthent   varchar2(1);',
' tmand      varchar2(10);       -- for mandatory',
' tseqmax    number;',
' nb_sections number;',
' display_selector varchar2(1) := ''N'';',
' t_template_options varchar2(1000);',
'item_template number;',
'l_last_update_on date;',
'tlov        varchar2(500);          -- inline lov',
't_position  VARCHAR2(100);          -- position used for rattaching a region',
'',
'begin ',
'',
'apex_debug_message.log_message(p_message=>''Start Form Generation'' ||tmsg);',
'',
'-- the application target must be set in GFM_SETUP table',
'-- under APP_RUN_ID parameter',
'',
'-- Get target flow_id of the runtime application',
'step := ''Get application Id'';',
'',
'l_flow_id := getParam(''APP_RUN_ID'');',
'',
'if l_flow_id is NULL then',
'    raise_application_error(-20202,''Parametre Id application Catalogue introuvable'');',
'end if;    ',
'if pid is null then',
'    select gfm_page_seq.NEXTVAL into l_page_id from dual;',
'else',
'    l_page_id := pid;    ',
'end if;',
'--select max(page_id) from apex_application_pages where application_id = 157 and page_id < 500 and page_id >100',
'',
'-- init default preferences',
'step := ''Get preferencs'';',
'prefs(''$ITEM_TEMPLATE'') := ''FLOATING'';          -- Template used for items',
'prefs(''$REGION_SECTION'') := ''TABS_CONTAINER'';   -- or DISPLAY_SELECTOR',
'prefs(''$ITEM_HELP'') := ''N'';                     -- Display item help',
'prefs(''$PAGE_TEMPLATE'') := ''STANDARD'';          -- template used for the main page',
'',
'-- ---------------------------------------------',
'-- Get Form preferences and fills an array',
'-- ---------------------------------------------',
'',
'for r in (select * from GFM_FORM_PREFS where form_id = fid) loop',
'        prefs(r.pref) := r.val;',
'end loop;',
'',
'-- --------------------------------------------',
'-- Retrieves templates id',
'-- --------------------------------------------',
'',
'step := ''Init templates'';',
'select distinct user_interface_id into l_user_interface_id',
'from apex_application_pages',
