');',
'',
'-- Analyzing the sequence of sections',
'',
'step := ''check number of sections'';',
'select count(DISTINCT section_id) into nb_sections',
'from GFM_ITEMS where form_id = fid;',
'apex_debug_message.log_message(p_message=>''NB_SECTIONS= '' ||nb_sections);',
'-- then we get the concatened list of sections. This list is supposed to be ordered.',
'-- Il all the sections are not present, we add them to the list.',
'if nb_sections >0 then',
'    select sections into tsections ',
'    from GFM_FORM ',
'    where id = fid;',
'',
'    if tsections IS NULL then   -- we build an ordered list of sections',
'        select listagg(distinct section, '':'') into tsections ',
'        from GFM_ITEMS where form_id = fid',
'        order by section ASC;',
'        update GFM_FORM set sections = tsections',
'        where id = fid;',
'        COMMIT;',
'    end if;',
'end if;',
'',
'-- If an item is not associated to a section we set it arbitrarily to ''$DUMMY'' value',
'subforms(''$DUMMY'') := l_form_region_id;',
'',
'if prefs(''$REGION_SECTION'') in (''DISPLAY_SELECTOR'', ''STANDARD'') and nb_sections >1 then          -- more than 2 sections',
'    l_plug_template := l_plug_template_std ;',
'elsif',
'    prefs(''$REGION_SECTION'') = ''remove'' and nb_sections >1 then          -- more than 2 sections ',
'    l_plug_template := l_plug_template_tabs;      ',
'end if;',
'',
'-- ----------- Creates main region for the Form itself',
'step := ''create main region'';',
'l_static_region_id   := wwv_flow_id.next_val;',
'l_form_region_id := l_static_region_id;     -- id of the main region',
'',
'wwv_flow_api.create_page_plug(',
'     p_id => l_static_region_id,',
'     p_flow_id  => l_flow_id,',
'     p_page_id  => l_page_id',
'    ,p_plug_name=>  NVL(form_rec.notes, form_rec.lib) -- ''fiche''||l_page_id',
'    --,p_region_template_options=>''#DEFAULT#:t-Region--scrollBody''',
'    ,p_region_template_options=>''#DEFAULT#:t-TabsRegion-mod--simple''',
'    ,p_plug_template=> l_plug_template',
'    ,p_plug_display_sequence=>0',
'    ,p_plug_display_point=>''BODY''',
'    ,p_query_type=>''TABLE''',
'    ,p_query_table=>''G$_''||form_rec.code',
'    ,p_include_rowid_column=>false',
'    ,p_is_editable=>true',
'    ,p_edit_operations=>''i:u:d''',
'    ,p_lost_update_check_type=>''VALUES''',
'    ,p_plug_source_type=>''NATIVE_FORM''',
');',
'',
'-- Creates subregions if exist',
'-- Each subregion will be displayed as a tab',
'-- Condition is that the number of sections is more than 1, otherwise, we ignore sections',
'',
'-- --------------------------------------------------',
'-- If section exist and if DISPLAY selector is active',
'-- then we create a region for display selector',
'-- --------------------------------------------------',
'',
'if prefs(''$REGION_SECTION'') = ''DISPLAY_SELECTOR'' and nb_sections >1 then          -- more than 2 sections',
'    step := ''create selector region'';',
