'        ,p_region_template_options=>''#DEFAULT#:t-Region--removeHeader js-removeLandmark:t-Region--noUI:t-Region--scrollBody''',
'        ,p_plug_template=> l_plug_template_std          -- standard template',
'        ,p_plug_display_sequence=>6                     -- arbitrary 6',
'        ,p_plug_display_point=>''BODY''',
'        ,p_plug_source_type=>''NATIVE_DISPLAY_SELECTOR''',
'        ,p_plug_query_options=>''DERIVED_REPORT_COLUMNS''',
'        ,p_attribute_01=>''STANDARD''',
'        ,p_attribute_02=>''Y''',
'        ,p_attribute_03=>''Y''',
'    );',
'    display_selector := ''Y'';',
'end if;',
'-- --------------------------------------------------',
'-- If section exist and if TABS_CONTAINER pref',
'-- then we create a region for Tabs Container',
'-- --------------------------------------------------',
'if prefs(''$REGION_SECTION'') = ''TABS_CONTAINER'' and nb_sections >1 then          -- more than 2 sections',
'    step := ''create tabs region'';',
'',
'    l_form_container_id   := wwv_flow_id.next_val;',
'',
'    wwv_flow_api.create_page_plug(',
'        p_id=> l_form_container_id                  -- wwv_flow_id.next_val',
'        ,p_flow_id  => l_flow_id',
'        ,p_page_id  => l_page_id',
'        ,p_plug_name=>''Tabs Container''',
'        ,p_parent_plug_id => l_form_region_id            -- attached to the main region',
'        ,p_region_template_options=>''#DEFAULT#''',
'        ,p_plug_template=> l_plug_template_tabs          -- Tabs Container template',
'        ,p_plug_display_sequence=>4                     -- arbitrary 4',
'        ,p_plug_display_point=>''SUB_REGIONS''',
'        ,p_plug_source_type=>''NATIVE_STATIC''',
'        ,p_plug_query_options=>''DERIVED_REPORT_COLUMNS''',
'        ,p_attribute_01=>''STANDARD''',
'        ,p_attribute_02=>''Y''',
'        ,p_attribute_03=>''Y''',
'    );',
'    l_form_region_id := l_form_container_id;  -- The tabs container region becomes the parent region',
'end if;',
'-- Creates a subregion for each sections if needed',
'nbsub := 0;',
'/*',
'for r in (select distinct i.SECTION,  DECODE(s.oseq,NULL,999,s.oseq) oseq',
'            from GFM_ITEMS i,',
'            (SELECT',
'            rownum *10 oseq,REGEXP_SUBSTR(tsections,''[^:]+'', 1, LEVEL) section',
'            FROM DUAL',
'            CONNECT BY LEVEL <= REGEXP_COUNT(tsections, '':'') + 1',
'            ) s         -- string SECTION is table enabled       ',
'            where form_id =fid',
'            and s.section(+) = i.section        -- avoid section column not synchronized with items',
'            and i.SECTION is not NULL',
'            order by oseq',
'            ) loop',
'     */       ',
'for r in (',
'        select section_name section, (rank*10) oseq',
'        from gfm_sections s',
'        where form_id=fid',
'        and exists (select 1 ',
'                    from gfm_items ',
'                    where form_id =fid and section_id = s.id',
'                   )',
'        order by rank',
'    ) loop',
'',
'',
'    nbsub := nbsub +1;          -- subregions counter',
'    tseq := tseq + 10;',
'    l_subform_id := wwv_flow_id.next_val;',
'    subforms(r.section) := l_subform_id ;   -- add the subregion in an array',
'',
'    -- Creates a subregion',
'',
'    if prefs(''$REGION_SECTION'') = ''STANDARD'' then',
'        t_template_options := ''#DEFAULT#:t-Region--noUI:t-Region--scrollBody:margin-top-md'';',
'        t_position := ''BODY'';',
'    elsif prefs(''$REGION_SECTION'') = ''DISPLAY_SELECTOR'' then',
'        t_position := ''BODY'';',
'        t_template_options := ''#DEFAULT#:t-Region--removeHeader js-removeLandmark:t-Region--noUI:t-Region--scrollBody:margin-top-md'';',
'    else',
'        --t_template_options := NULL;',
'   ',
'        t_template_options := ''#DEFAULT#'';',
'        t_position := ''SUB_REGIONS'';',
'    end if;',
'    wwv_flow_api.create_page_plug(',
'        p_id    =>l_subform_id',
'        ,p_flow_id  => l_flow_id',
'        ,p_page_id  => l_page_id',
'        ,p_plug_name =>r.SECTION',
'        ,p_parent_plug_id=> l_form_region_id            -- attached to the main region',
'        --,p_region_template_options=>''#DEFAULT#:t-Region--scrollBody''',
'        -- we choose a non header subregion and minimal decoration',
'        ,p_region_template_options=> t_template_options',
'        ,p_plug_template=>l_plug_template',
'        ,p_plug_display_sequence=> r.oseq',
'        ,p_include_in_reg_disp_sel_yn=> display_selector',
'        ,p_plug_display_point=> t_position          -- ''BODY''',
'        ,p_plug_query_options=>''DERIVED_REPORT_COLUMNS''',
'        ,p_attribute_01=>''N''',
'        ,p_attribute_02=>''HTML''',
'    );',
'',
'end loop;',
