'-- loop on other items',
'',
'step := ''create others items'';',
'',
'--select items into titems        -- get ordered list of items',
'--from GFM_FORM ',
'--where id = fid;',
'',
'-- make sure that items are ordered',
'step := ''create others items/1'';',
'select max(SEQ) into tseqmax from GFM_ITEMS',
'where FORM_ID = fid;',
'',
'for r in (  select ROWNUM nb, i.* ',
'            from GFM_ITEMS i  ',
'            where FORM_ID = fid and seq IS NULL) loop',
'',
'    update GFM_ITEMS set SEQ = tseqmax + (r.nb *10)',
'    where ID = r.ID;',
'end loop;',
'',
'step := ''create others items/2'';',
'for r in (  select  i.id,',
'                    i.code,',
'                    c.ctype,        -- used for item type',
'                    c.lib complib,',
'                    i.lov,          -- inline lov ',
'                    DECODE (c.ctype, ''CN'',1000,60) siz,',
'                    DECODE (c.ctype,''CN'', 3,1) height,   ',
'                    DECODE(c.ctype, ',
'                        ''CN'', ''NATIVE_TEXTAREA'',',
'                        ''DA'', ''NATIVE_DATE_PICKER_JET'',',
'                        ''CT'', ''NATIVE_TEXT_FIELD''',
'                    ) atype,',
'                    i.lib,          -- used for label',
'                    NVL((select section_name from GFM_SECTIONS where id=section_id),''$DUMMY'') section,',
'                    --NVL(i.section, ''$DUMMY'') section,      -- used for Tabs',
'                    i.vtype,         -- display type ',
'                    --NVL(s.oseq, 10) oseq,         -- sequence',
'                    NVL(i.SEQ,10) oseq,',
'                    i.new_row,       -- new line ?',
'                    ',
'                    DECODE (i.MANDATORY, ',
'                                ''Y'', DECODE(prefs(''$ITEM_TEMPLATE''), ',
'                                            ''FLOATING'', itemplates(''REQUIRED_FLOATING''),',
'                                            ''BESIDE'', itemplates(''REQUIRED''),',
'                                            ''ABOVE'', itemplates(''REQUIRED_ABOVE'')',
'                                            ), ',
'                                ''N'',  DECODE(prefs(''$ITEM_TEMPLATE''), ',
'                                            ''FLOATING'', itemplates(''OPTIONAL_FLOATING''),',
'                                            ''BESIDE'', itemplates(''OPTIONAL''),',
'                                            ''ABOVE'', itemplates(''OPTIONAL_ABOVE'')',
'                                            )',
'                            ) field_template,',
'                    ',
'                    --DECODE (i.MANDATORY, ''Y'', itemplates(''REQUIRED_FLOATING''), ''N'', itemplates(''OPTIONAL_FLOATING'')) field_template,',
'                    DECODE(c.CTYPE, ''NB'', ''#DEFAULT#'',',
'                        DECODE (i.MANDATORY, ''Y'', ''#DEFAULT#:t-Form-fieldContainer--indicatorAsterisk'', ''N'', ''#DEFAULT#'')',
'                     ) template_options,',
'                    case when (col_span = 0 or col_span is null) ',
'                        then NULL ',
'                        else',
'                        (i.col_span) *2 ',
'                        end  col_span,     -- how many columns',
