'',
'function get_lov (id number) ',
'return varchar2',
'as',
'    req varchar2(1000);',
'    tctype varchar2(10);',
'    tobj varchar2(50);',
'    tid varchar2(100);',
'begin',
'    tid := id;',
'    -- is the list base on a external view or on the internal view',
'    select ctype, obj_source into tctype, tobj ',
'    from GFM_COMP',
'    where id in (select comp from GFM_ITEMS',
'                            where id = tid',
'                    ); ',
'    if tctype = ''LI'' then',
'        req := ''',
'        select ',
'        LIB_VALUE d,',
'        C_VALUE r',
'        from GFM_LOV',
'        where comp_id in (select comp from GFM_ITEMS',
'                            where id = ''||tid||'') ',
'        order by LIB_VALUE ''',
'        ;',
'    else            -- Dynamic List',
'        req := ''',
'        select d, r',
'        from '' || tobj || '' order by d''',
'        ;',
'    end if;',
'    return req;',
'end;',
'',
'function get_lov (plov varchar2) ',
'return varchar2',
'as',
'    req varchar2(1000);',
'    tctype varchar2(10);',
'    tobj varchar2(50);',
'    tid varchar2(100);',
'begin',
'    ',
'        req := ''',
'        select ',
'        LIB_VALUE d,',
'        C_VALUE r',
'        from GFM_LOV',
'        where comp_id in (select id from GFM_COMP',
'                            where lib = ''''''||plov||'''''') ',
'        order by LIB_VALUE ''',
'        ;',
'    ',
'    return req;',
'end;',
'',
'',
'function get_lov_master (id varchar2) ',
'return varchar2',
'as',
'    req varchar2(1000);',
'begin',
'    req := ''',
'    select ',
'    LIB_VALUE d,',
'    C_VALUE r',
'    from GFM_LOV',
'    where comp_id = ''''''|| id || ''''''',
'    order by LIB_VALUE''',
'    ;',
'    return req;',
'end;',
'',
'function getNewPageid (fid number)',
'return number',
'as',
'tid number;',
'begin',
'    select min(slot) into tid',
'    from (',
'    SELECT  LEVEL + 100 slot',
'    FROM DUAL',
'    CONNECT BY LEVEL <= 300',
'    ) a',
'    where',
'    not exists (select 1 from ',
'                    (select',
'                        page_id nb ',
'                        from APEX_APPLICATION_PAGES',
'                        where APPLICATION_ID = fid',
'                        and PAGE_ID >100 and page_id < 500',
'                        and PAGE_ID = a.slot',
'                        UNION',
'                        select PAGE_ID nb',
'                        from GFM_FORM',
'                        where PAGE_ID = a.slot ',
'                    )',
'                );',
'return tid;',
'end;',
'',
'function copyForm(fid number, appid number)',
'return number',
'is',
'tid number;',
'tpid number;',
'mid number;',
'seq_name varchar2(200);',
'    ',
'begin',
'',
'-- find the sequence name ',
'-- Returning Values doesn''t work with a insert ... select',
'select sequence_name into seq_name',
'from user_tab_identity_cols',
'where table_name = ''GFM_FORM''',
'and column_name = ''ID''; ',
'     ',
'execute immediate ''select ''||seq_name||''.nextval from dual'' into tid;',
'',
'tpid := GFM_BUILDER.getNewPageId(appid);',
'insert into GFM_FORM (',
'       ID,',
'       LIB,',
'       NOTES,',
'       SCOPE,',
'       CODE,',
'       PAGE_ID,',
'       SECTIONS,',
'       ITEMS,',
'       IS_PUBLISHED,',
'       IS_FORM_GENERATED,',
'       IS_VIEW_GENERATED,',
'       VTYPE,',
'       AUTHENT,',
'       MAP_LASTID',
')',
'select ',
'       tid,',
'       LIB ||''_''||to_char(SYSDATE,''HH24MISS''),',
'       NOTES,',
'       SCOPE,',
'       CODE||''_''||to_char(SYSDATE,''HH24MISS''),',
'       tpid,',
'       SECTIONS,',
'       ITEMS,',
'       IS_PUBLISHED,',
'       IS_FORM_GENERATED,',
'       IS_VIEW_GENERATED,',
'       VTYPE,',
'       AUTHENT,',
'       MAP_LASTID',
'from "GFM_FORM" ',
'where id = fid;',
'',
'-- TBD ! Copy sections',
'',
'insert into GFM_SECTIONS(',
'       FORM_ID,',
'       SECTION_NAME,',
'       RANK',
')',
'select ',
'       tid,',
'       SECTION_NAME,',
'       RANK',
'from GFM_SECTIONS',
'where FORM_ID = fid ;',
' -- Copy Items'))
);
null;
end;
