'begin',
'/*',
'--select * from apex_application_pages where user_interface_id = 13238501903945206',
'',
'select distinct user_interface_id into l_user_interface_id from apex_application_pages',
'where application_id = l_flow_id; ',
'',
'select template_id into l_plug_template from apex_application_templates',
'where template_type = ''Region'' and internal_name = ''STANDARD'' and application_id = l_flow_id;',
'select template_id into  l_plug_template_tabs from apex_application_templates',
'where template_type = ''Region'' and internal_name = ''TABS_CONTAINER'' and application_id = l_flow_id;',
'',
'select template_id into l_button_template_id from apex_application_templates',
'where template_type = ''Button'' and internal_name = ''TEXT'' and application_id = l_flow_id;',
'',
'select template_id into l_field_template from apex_application_templates',
'where template_type = ''Item Label'' and internal_name = ''OPTIONAL_FLOATING'' and application_id = l_flow_id;',
'',
' l_user_interface_id    NUMBER := 13238501903945206;      -- Page STANDARD --13238501903945206;',
' l_plug_template        NUMBER := 13148911441945113;    -- Region	STANDARD',
' l_plug_template_tabs   NUMBER := 13155707099945116;    -- Region	TABS_CONTAINER',
' l_button_template_id   NUMBER := 13214017098945164;    -- Button	TEXT',
' l_field_template       NUMBER := 13211557155945161;    -- ''Item Label''	OPTIONAL_FLOATING',
'*/',
'null;',
'end;',
'',
'-- -------------------------------------------------------------',
'-- MAIN MODULE',
'-- -------------------------------------------------------------',
'',
'function generate_form(fid number                       -- form id',
'                        , pid number DEFAULT null   )   -- page id',
'return number',
'is',
' l_static_region_id   NUMBER;',
' l_region_id          NUMBER;',
' l_form_region_id     NUMBER;',
' l_form_container_id  NUMBER;',
' l_subform_id         NUMBER;',
' type subforms_id IS VARRAY(10) OF NUMBER;',
' TYPE subforms_array_type ',
'    IS TABLE OF NUMBER ',
'    INDEX BY VARCHAR2(100);',
' subforms  subforms_array_type;  ',
'',
' TYPE template_array_type ',
'    IS TABLE OF NUMBER ',
'    INDEX BY VARCHAR2(100);',
' itemplates  template_array_type; ',
'',
'TYPE prefs_array_type ',
'    IS TABLE OF VARCHAR2(100) ',
'    INDEX BY VARCHAR2(100);',
'prefs prefs_array_type;',
'',
' type items_array_type IS VARRAY(100) OF NUMBER;',
' items_arr   items_array_type := items_array_type(); ',
' r_prefs GFM_FORM_PREFS%ROWTYPE;',
' pref_item_template VARCHAR2(100);',
'',
' -- Template id',
' l_user_interface_id    NUMBER; -- := 13238501903945206;    -- Page STANDARD ',
' l_plug_template        NUMBER; -- := 13148911441945113;    -- Region STANDARD',
' l_plug_template_tabs   NUMBER; -- := 13155707099945116;    -- Region TABS_CONTAINER',
' l_button_template_id   NUMBER; -- := 13214017098945164;    -- Button TEXT',
' l_field_template       NUMBER; -- := 13211557155945161;    -- Item Label	OPTIONAL_FLOATING',
' l_field_optional       NUMBER; -- Item Label OPTIONAL',
' l_plug_template_std    NUMBER; -- Main Region',
' l_step_template_id     NUMBER; -- used for page template',
'',
' lov_array  apex_t_varchar2;',
' ',
' test       varchar2(10);',
' l_flow_id  NUMBER;           ',
' l_page_id  NUMBER;',
' step       varchar2(50);',
' l_id       NUMBER;',
' tseq       NUMBER := 0;',
' form_rec   GFM_FORM%ROWTYPE;',
' tdisplay   varchar2(100);',
' attr1      varchar2(100);',
' attr2      varchar2(100);',
' attr3      varchar2(100);',
' nbsub      number := 0;        -- subregions counter',
' subregion_id number;',
' tsections  varchar2(1000);',
' titems     varchar2(1000);',
' tmsg       varchar2(1000);',
' ttemplate  number;',
' tlist      varchar2(4000);',
' delete_id  number;',
' ig_region_id number;',
' ig_id      number;',
' report_id  number;',
' view_id    number;',
' static_id  varchar2(10);',
' tid        number;',
' i          number;',
' vreport_id number;             -- report id get from view ',
' vview_id   number;             -- view id get from view ',
' tauthent   varchar2(1);',
' tmand      varchar2(10);       -- for mandatory',
' tseqmax    number;',
' nb_sections number;',
' display_selector varchar2(1) := ''N'';',
' t_template_options varchar2(1000);',
'item_template number;',
'l_last_update_on date;',
'tlov        varchar2(500);          -- inline lov',
't_position  VARCHAR2(100);          -- position used for rattaching a region',
'',
'begin ',
'',
'apex_debug_message.log_message(p_message=>''Start Form Generation'' ||tmsg);',
'',
'-- the application target must be set in GFM_SETUP table',
'-- under APP_RUN_ID parameter',
'',
'-- Get target flow_id of the runtime application',
'step := ''Get application Id'';',
'',
'l_flow_id := getParam(''APP_RUN_ID'');',
'',
'if l_flow_id is NULL then',
'    raise_application_error(-20202,''Parametre Id application Catalogue introuvable'');',
'end if;    ',
'if pid is null then',
'    select gfm_page_seq.NEXTVAL into l_page_id from dual;',
'else',
'    l_page_id := pid;    ',
'end if;',
'--select max(page_id) from apex_application_pages where application_id = 157 and page_id < 500 and page_id >100',
'',
'-- init default preferences',
'step := ''Get preferencs'';',
'prefs(''$ITEM_TEMPLATE'') := ''FLOATING'';          -- Template used for items',
'prefs(''$REGION_SECTION'') := ''TABS_CONTAINER'';   -- or DISPLAY_SELECTOR',
'prefs(''$ITEM_HELP'') := ''N'';                     -- Display item help',
'prefs(''$PAGE_TEMPLATE'') := ''STANDARD'';          -- template used for the main page',
'',
'-- ---------------------------------------------',
'-- Get Form preferences and fills an array',
'-- ---------------------------------------------',
'',
'for r in (select * from GFM_FORM_PREFS where form_id = fid) loop',
'        prefs(r.pref) := r.val;',
'end loop;',
'',
'-- --------------------------------------------',
'-- Retrieves templates id',
'-- --------------------------------------------',
'',
'step := ''Init templates'';',
'select distinct user_interface_id into l_user_interface_id',
'from apex_application_pages',
'where application_id = l_flow_id; ',
'',
'select template_id into l_step_template_id ',
'from apex_application_templates',
'where template_type = ''Page'' and internal_name = prefs(''$PAGE_TEMPLATE'')',
'and application_id = l_flow_id;',
'',
'select template_id into l_plug_template_std ',
'from apex_application_templates',
'where template_type = ''Region'' and internal_name = ''STANDARD'' and application_id = l_flow_id;',
'',
'l_plug_template := l_plug_template_std;',
'',
'select template_id into  l_plug_template_tabs ',
'from apex_application_templates',
'where template_type = ''Region'' ',
'and internal_name = ''TABS_CONTAINER'' and application_id = l_flow_id;',
'',
'select template_id into l_button_template_id ',
'from apex_application_templates',
'where template_type = ''Button'' and internal_name = ''TEXT'' and application_id = l_flow_id;',
'',
'select template_id into l_field_template ',
'from apex_application_templates',
'where template_type = ''Item Label'' ',
'and internal_name =  DECODE (prefs(''$ITEM_TEMPLATE''), ',
'                                ''BESIDE'', ''OPTIONAL'', ',
'                                ''FLOATING'', ''OPTIONAL_FLOATING'',',
'                                ''ABOVE'', ''OPTIONAL_ABOVE'',',
'                                ''OPTIONAL_FLOATING''     --''OPTIONAL_FLOATING'' default',
'                            )',
'and application_id = l_flow_id;',
'',
'select template_id into l_field_optional from apex_application_templates',
'where template_type = ''Item Label'' and internal_name = ''OPTIONAL'' and application_id = l_flow_id;',
'',
'select template_id into itemplates(''OPTIONAL_FLOATING'') from apex_application_templates',
'where template_type = ''Item Label'' ',
'and internal_name = ''OPTIONAL_FLOATING'' ',
'and application_id = l_flow_id;',
'',
'select template_id into itemplates(''REQUIRED_FLOATING'') from apex_application_templates',
'where template_type = ''Item Label'' ',
'and internal_name = ''REQUIRED_FLOATING'' ',
'and application_id = l_flow_id;',
'',
'select template_id into itemplates(''OPTIONAL_ABOVE'') from apex_application_templates',
'where template_type = ''Item Label'' ',
'and internal_name = ''OPTIONAL_ABOVE'' ',
'and application_id = l_flow_id;',
'',
'select template_id into itemplates(''REQUIRED_ABOVE'') from apex_application_templates',
'where template_type = ''Item Label'' ',
'and internal_name = ''REQUIRED_ABOVE'' ',
'and application_id = l_flow_id;',
'',
'select template_id into itemplates(''OPTIONAL'') from apex_application_templates',
'where template_type = ''Item Label'' and internal_name = ''OPTIONAL'' and application_id = l_flow_id;',
'',
'select template_id into itemplates(''REQUIRED'') from apex_application_templates',
'where template_type = ''Item Label'' and internal_name = ''REQUIRED'' and application_id = l_flow_id;',
'',
'',
'-- --------------------------------------------',
'-- Get Form properties',
'-- --------------------------------------------',
'',
'select * into form_rec',
'from GFM_FORM',
'where id = fid;',
'',
'if form_rec.authent = ''Y'' then ',
'    tauthent := ''N'';',
'else',
'    tauthent := ''Y'';    ',
'end if;',
'',
'-- remove existing page ',
'-- The page will be removed before any re-generation',
'-- Caution: Any custom code will be lost',
'step := ''remove page'';',
'',
'wwv_flow_api.remove_page (p_flow_id=>l_flow_id, p_page_id=>l_page_id);',
'',
'-- Create a new page',
'',
'step := ''create page'';',
'wwv_flow_api.create_page(',
'     p_flow_id     => l_flow_id',
'    ,p_id => l_page_id',
'    ,p_user_interface_id=> l_user_interface_id',
'    ,p_name=> form_rec.lib',
'    ,p_alias=>replace(form_rec.lib,'' '','''')',
'    ,p_step_title=>form_rec.lib',
'    ,p_step_template=> l_step_template_id',
'    ,p_autocomplete_on_off=>''OFF''',
'    ,p_javascript_code=>''var htmldb_delete_message=''''"DELETE_CONFIRM_MSG"'''';''',
'    ,p_inline_css=>wwv_flow_string.join(wwv_flow_t_varchar2(',
'    ''.t-Form-fieldContainer:not(.t-Form-fieldContainer--stacked):not(.t-Form-fieldContainer--floatingLabel) .apex-item-group--rc input + label {'',',
'    ''    margin-top: 14px;'',',
'    ''    margin-bottom: 8px;'',',
'    ''}''))       -- for radio group alignement in redwood Light Optional v.21.1',
'    ,p_page_template_options=>''#DEFAULT#''',
'    --,p_protection_level=>''C'',',
'    ,p_page_is_public_y_n=> tauthent',
'    ,p_last_updated_by=> v(''APP_USER'')',
'    ,p_last_upd_yyyymmddhh24miss=> TO_CHAR(SYSDATE,''YYYYMMDDHH24MISS'')--''20211023185021''',
');',
'',
'-- Analyzing the sequence of sections',
'',
'step := ''check number of sections'';',
'select count(DISTINCT section_id) into nb_sections',
'from GFM_ITEMS where form_id = fid;',
'apex_debug_message.log_message(p_message=>''NB_SECTIONS= '' ||nb_sections);',
'-- then we get the concatened list of sections. This list is supposed to be ordered.',
'-- Il all the sections are not present, we add them to the list.',
'if nb_sections >0 then',
'    select sections into tsections ',
'    from GFM_FORM ',
'    where id = fid;',
'',
'    if tsections IS NULL then   -- we build an ordered list of sections',
'        select listagg(distinct section, '':'') into tsections ',
'        from GFM_ITEMS where form_id = fid',
'        order by section ASC;',
'        update GFM_FORM set sections = tsections',
'        where id = fid;',
'        COMMIT;',
'    end if;',
'end if;',
'',
'-- If an item is not associated to a section we set it arbitrarily to ''$DUMMY'' value',
'subforms(''$DUMMY'') := l_form_region_id;',
'',
'if prefs(''$REGION_SECTION'') in (''DISPLAY_SELECTOR'', ''STANDARD'') and nb_sections >1 then          -- more than 2 sections',
'    l_plug_template := l_plug_template_std ;',
'elsif',
'    prefs(''$REGION_SECTION'') = ''remove'' and nb_sections >1 then          -- more than 2 sections ',
'    l_plug_template := l_plug_template_tabs;      ',
'end if;',
'',
'-- ----------- Creates main region for the Form itself',
'step := ''create main region'';',
'l_static_region_id   := wwv_flow_id.next_val;',
'l_form_region_id := l_static_region_id;     -- id of the main region',
'',
'wwv_flow_api.create_page_plug(',
'     p_id => l_static_region_id,',
'     p_flow_id  => l_flow_id,',
'     p_page_id  => l_page_id',
'    ,p_plug_name=>  NVL(form_rec.notes, form_rec.lib) -- ''fiche''||l_page_id',
'    --,p_region_template_options=>''#DEFAULT#:t-Region--scrollBody''',
'    ,p_region_template_options=>''#DEFAULT#:t-TabsRegion-mod--simple''',
'    ,p_plug_template=> l_plug_template',
'    ,p_plug_display_sequence=>0',
'    ,p_plug_display_point=>''BODY''',
'    ,p_query_type=>''TABLE''',
'    ,p_query_table=>''G$_''||form_rec.code',
'    ,p_include_rowid_column=>false',
'    ,p_is_editable=>true',
'    ,p_edit_operations=>''i:u:d''',
'    ,p_lost_update_check_type=>''VALUES''',
'    ,p_plug_source_type=>''NATIVE_FORM''',
');',
'',
'-- Creates subregions if exist',
'-- Each subregion will be displayed as a tab',
'-- Condition is that the number of sections is more than 1, otherwise, we ignore sections',
'',
'-- --------------------------------------------------',
'-- If section exist and if DISPLAY selector is active',
'-- then we create a region for display selector',
'-- --------------------------------------------------',
'',
'if prefs(''$REGION_SECTION'') = ''DISPLAY_SELECTOR'' and nb_sections >1 then          -- more than 2 sections',
'    step := ''create selector region'';',
'    wwv_flow_api.create_page_plug(',
'        p_id=>wwv_flow_id.next_val',
'        ,p_flow_id  => l_flow_id',
'        ,p_page_id  => l_page_id',
'        ,p_plug_name=>''Selector''',
'        ,p_parent_plug_id => l_form_region_id            -- attached to the main region',
'        ,p_region_template_options=>''#DEFAULT#:t-Region--removeHeader js-removeLandmark:t-Region--noUI:t-Region--scrollBody''',
'        ,p_plug_template=> l_plug_template_std          -- standard template',
'        ,p_plug_display_sequence=>6                     -- arbitrary 6',
'        ,p_plug_display_point=>''BODY''',
'        ,p_plug_source_type=>''NATIVE_DISPLAY_SELECTOR''',
'        ,p_plug_query_options=>''DERIVED_REPORT_COLUMNS''',
'        ,p_attribute_01=>''STANDARD''',
'        ,p_attribute_02=>''Y''',
'        ,p_attribute_03=>''Y''',
'    );',
'    display_selector := ''Y'';',
'end if;',
'-- --------------------------------------------------',
'-- If section exist and if TABS_CONTAINER pref',
'-- then we create a region for Tabs Container',
'-- --------------------------------------------------',
'if prefs(''$REGION_SECTION'') = ''TABS_CONTAINER'' and nb_sections >1 then          -- more than 2 sections',
'    step := ''create tabs region'';',
'',
'    l_form_container_id   := wwv_flow_id.next_val;',
'',
'    wwv_flow_api.create_page_plug(',
'        p_id=> l_form_container_id                  -- wwv_flow_id.next_val',
'        ,p_flow_id  => l_flow_id',
'        ,p_page_id  => l_page_id',
'        ,p_plug_name=>''Tabs Container''',
'        ,p_parent_plug_id => l_form_region_id            -- attached to the main region',
'        ,p_region_template_options=>''#DEFAULT#''',
'        ,p_plug_template=> l_plug_template_tabs          -- Tabs Container template',
'        ,p_plug_display_sequence=>4                     -- arbitrary 4',
'        ,p_plug_display_point=>''SUB_REGIONS''',
'        ,p_plug_source_type=>''NATIVE_STATIC''',
'        ,p_plug_query_options=>''DERIVED_REPORT_COLUMNS''',
'        ,p_attribute_01=>''STANDARD''',
'        ,p_attribute_02=>''Y''',
'        ,p_attribute_03=>''Y''',
'    );',
'    l_form_region_id := l_form_container_id;  -- The tabs container region becomes the parent region',
'end if;',
'-- Creates a subregion for each sections if needed',
'nbsub := 0;',
'/*',
'for r in (select distinct i.SECTION,  DECODE(s.oseq,NULL,999,s.oseq) oseq',
'            from GFM_ITEMS i,',
'            (SELECT',
'            rownum *10 oseq,REGEXP_SUBSTR(tsections,''[^:]+'', 1, LEVEL) section',
'            FROM DUAL',
'            CONNECT BY LEVEL <= REGEXP_COUNT(tsections, '':'') + 1',
'            ) s         -- string SECTION is table enabled       ',
'            where form_id =fid',
'            and s.section(+) = i.section        -- avoid section column not synchronized with items',
'            and i.SECTION is not NULL',
'            order by oseq',
'            ) loop',
'     */       ',
'for r in (',
'        select section_name section, (rank*10) oseq',
'        from gfm_sections s',
'        where form_id=fid',
'        and exists (select 1 ',
'                    from gfm_items ',
'                    where form_id =fid and section_id = s.id',
'                   )',
'        order by rank',
'    ) loop',
'',
'',
'    nbsub := nbsub +1;          -- subregions counter',
'    tseq := tseq + 10;',
'    l_subform_id := wwv_flow_id.next_val;',
'    subforms(r.section) := l_subform_id ;   -- add the subregion in an array',
'',
'    -- Creates a subregion',
'',
'    if prefs(''$REGION_SECTION'') = ''STANDARD'' then',
'        t_template_options := ''#DEFAULT#:t-Region--noUI:t-Region--scrollBody:margin-top-md'';',
'        t_position := ''BODY'';',
'    elsif prefs(''$REGION_SECTION'') = ''DISPLAY_SELECTOR'' then',
'        t_position := ''BODY'';',
'        t_template_options := ''#DEFAULT#:t-Region--removeHeader js-removeLandmark:t-Region--noUI:t-Region--scrollBody:margin-top-md'';',
'    else',
'        --t_template_options := NULL;',
'   ',
'        t_template_options := ''#DEFAULT#'';',
'        t_position := ''SUB_REGIONS'';',
'    end if;',
'    wwv_flow_api.create_page_plug(',
'        p_id    =>l_subform_id',
'        ,p_flow_id  => l_flow_id',
'        ,p_page_id  => l_page_id',
'        ,p_plug_name =>r.SECTION',
'        ,p_parent_plug_id=> l_form_region_id            -- attached to the main region',
'        --,p_region_template_options=>''#DEFAULT#:t-Region--scrollBody''',
'        -- we choose a non header subregion and minimal decoration',
'        ,p_region_template_options=> t_template_options',
'        ,p_plug_template=>l_plug_template',
'        ,p_plug_display_sequence=> r.oseq',
'        ,p_include_in_reg_disp_sel_yn=> display_selector',
'        ,p_plug_display_point=> t_position          -- ''BODY''',
'        ,p_plug_query_options=>''DERIVED_REPORT_COLUMNS''',
'        ,p_attribute_01=>''N''',
'        ,p_attribute_02=>''HTML''',
'    );',
'',
'end loop;',
'',
'-- --------------------------------------------',
'-- creates buttons',
'-- --------------------------------------------',
' step := ''create button'';',
' wwv_flow_api.create_page_button(',
'     p_id=>wwv_flow_id.next_val',
'     ,p_flow_id  => l_flow_id',
'     ,p_flow_step_id => l_page_id',
'    ,p_button_sequence=>30',
'    ,p_button_plug_id => l_static_region_id',
'    ,p_button_name=>''SAVE''',
'    ,p_button_action=>''SUBMIT''',
'    ,p_button_template_options=>''#DEFAULT#''',
'    ,p_button_template_id=> l_button_template_id',
'    ,p_button_is_hot=>''Y''',
'    ,p_button_image_alt=>''Apply Changes''',
'    --,p_button_position=>''REGION_TEMPLATE_CHANGE''',
'    ,p_button_position=>''TOP''',
'    ,p_button_condition=>''P''||l_page_id||''_UPD_ID''',
'    ,p_button_condition_type=>''ITEM_IS_NOT_NULL''',
'    ,p_database_action=>''UPDATE''',
');',
'',
'wwv_flow_api.create_page_button(',
'     p_id=>wwv_flow_id.next_val',
'    ,p_flow_id  => l_flow_id',
'    ,p_flow_step_id => l_page_id',
'    ,p_button_sequence=>10',
'    ,p_button_plug_id=>l_static_region_id',
'    ,p_button_name=>''CANCEL''',
'    ,p_button_action=>''REDIRECT_PAGE''',
'    ,p_button_template_options=>''#DEFAULT#''',
'    ,p_button_template_id=>l_button_template_id',
'    ,p_button_image_alt=>''Cancel''',
'    --,p_button_position=>''REGION_TEMPLATE_CLOSE''',
'    ,p_button_position=>''TOP''',
'    --,p_button_redirect_url=>''f?p=&APP_ID.:1:&SESSION.::&DEBUG.:::''',
'    ,p_button_redirect_url=>''javascript:window.close();''',
');',
'wwv_flow_api.create_page_button(',
'     p_id=>wwv_flow_id.next_val',
'    ,p_flow_id  => l_flow_id',
'    ,p_flow_step_id => l_page_id',
'    ,p_button_sequence=>40',
'    ,p_button_plug_id=>l_static_region_id',
'    ,p_button_name=>''CREATE''',
'    ,p_button_action=>''SUBMIT''',
'    ,p_button_template_options=>''#DEFAULT#''',
'    ,p_button_template_id=>l_button_template_id',
'    ,p_button_is_hot=>''Y''',
'    ,p_button_image_alt=>''Submit''',
'    --,p_button_position=>''REGION_TEMPLATE_CREATE''',
'    ,p_button_position=>''TOP''',
'    ,p_button_condition=>''P''||l_page_id||''_upd_ID''',
'    ,p_button_condition_type=>''ITEM_IS_NULL''',
'    ,p_database_action=>''INSERT''',
');',
'delete_id := wwv_flow_id.next_val;',
'',
'wwv_flow_api.create_page_button(',
'     p_id=> delete_id',
'    ,p_flow_id  => l_flow_id',
'    ,p_flow_step_id => l_page_id',
'    ,p_button_sequence=>20',
'    ,p_button_plug_id=>l_static_region_id',
'    ,p_button_name=>''DELETE''',
'    ,p_button_action=>''REDIRECT_URL''',
'    ,p_button_template_options=>''#DEFAULT#''',
'    ,p_button_template_id=>l_button_template_id',
'    ,p_button_image_alt=>''Delete''',
'    --,p_button_position=>''REGION_TEMPLATE_DELETE''',
'    ,p_button_position=>''TOP''',
'    ,p_button_redirect_url=>''javascript:apex.confirm(htmldb_delete_message,''''DELETE'''');''',
'    ,p_button_execute_validations=>''N''',
'    ,p_button_condition=>''P''||l_page_id||''_UPD_ID''',
'    ,p_button_condition_type=>''ITEM_IS_NOT_NULL''',
'    ,p_database_action=>''DELETE''',
');',
'',
'-- creates branch',
'',
'wwv_flow_api.create_page_branch(',
