'',
'procedure generate_ddl (fid number, ptype varchar2 default ''M'')',
'is',
'',
'    req     varchar2(32000);',
'    req2    varchar2(32000);',
'    i       number := 0;',
'    vname   varchar2(100);',
'    trgname varchar2(100);',
'    tcname  varchar2(10);',
'    form_rec GFM_FORM%ROWTYPE;',
'    tmaxid  number;',
'begin',
'',
'    select * into form_rec',
'    from GFM_FORM',
'    where id = fid;',
'',
'',
'    -- get the last id given',
'    select MAP_LASTID ',
'    into tmaxid',
'    from GFM_FORM',
'    where ID = fid;',
'    if tmaxid is null then ',
'        tmaxid :=0;',
'    end if;',
'',
'    -- assign a column name if this task hasn''t been done before',
'    update GFM_ITEMS',
'    set MAP_ID = tmaxid + rownum',
'    where  FORM_ID = fid and MAP_ID IS NULL;',
'',
'    -- get the last id given in this run',
'    select max(map_id) into tmaxid',
'    from GFM_ITEMS',
'    where FORM_ID = fid;',
'',
'    -- update the form',
'    update GFM_FORM',
'    set MAP_LASTiD = tmaxid',
'    where ID = fid;',
'',
'    -- Create the support view',
'    vname := upper(''G$_''||replace(form_rec.code,'' '',''_''));',
'    req := ''create  or replace view ''|| vname || '' ('';',
'    req := req ||''seq_id,'';',
'    for r in (select * from GFM_ITEMS ',
'                where form_id = fid',
'                and TCOMP = ''C''',
'                order by MAP_ID',
'                ) loop',
'        if i>0 then',
'            req := req ||'','';',
'        end if;',
'        i := i+1;',
'        req := req || r.CODE ;',
'        --req := req || r.CODE;',
'    end loop;',
'',
'    if ptype = ''D'' then         -- If it''s an item Detail with a master table',
'        req := req ||'', parent_id'';',
'    end if;    ',
'    req := req ||'',form_id) as select seq_id, '';',
'    i := 0;',
'    for r in (select * from GFM_ITEMS ',
'              where form_id = fid and tcomp = ''C''',
'              order by MAP_ID) loop',
'        if i>0 then',
'            req := req ||'','';',
'        end if;',
'        i := i+1;',
'        req := req || ''C0''||lpad(to_char(r.map_id),2,''0'');',
'        --req := req || r.CODE;',
'    end loop;',
'    if ptype = ''D'' then',
'        req := req ||'', N001'';',
'    end if; ',
'    req := req || '', form_id from GFM_DATA where form_id=''||fid;',
'',
'    EXECUTE IMMEDIATE req;',
'',
'    -- Add primary key',
'    req := ''alter view ''|| vname ||'' add constraint ''|| vname ||''_PK primary key (SEQ_ID) disable'';',
'    EXECUTE IMMEDIATE req;',
'',
'    -- add trigger',
'    trgname := vname ||''_TRG'';',
'    req := ',
'    ''CREATE OR REPLACE TRIGGER ''||trgname ||''',
'    INSTEAD OF INSERT ON ''||vname||'' ',
'    BEGIN',
'        insert into GFM_DATA (collection_name,form_id, seq_id,'';',
'',
'    i := 0;    ',
'    for r in (select * from GFM_ITEMS ',
'              where form_id = fid and tcomp = ''C''',
'              order by MAP_ID) loop',
'        if i>0 then',
'            req := req ||'','';',
'        end if;',
'        i := i+1;',
'        req := req || ''C0''||lpad(to_char(r.map_id),2,''0'');',
'        --req := req || r.CODE;',
'    end loop;',
'    if ptype = ''D'' then',
'        req := req ||'', N001'';',
'    end if;',
'    req := req ||'') values ',
'    (''''''|| form_rec.code ||'''''','' ||fid ||'', :new.SEQ_ID,'';',
'    i := 0;',
'    for r in (select * from GFM_ITEMS ',
'              where form_id = fid and tcomp = ''C''',
'              order by MAP_ID) loop',
'        if i>0 then',
'            req := req ||'','';',
'        end if;',
'        i := i+1;',
'        req := req || '':new.''|| r.CODE ;',
'        --req := req || r.CODE;',
'    end loop;',
'    if ptype = ''D'' then',
'        req := req ||'', :new.parent_id'';',
'    end if;',
'    req := req || ''); END;'';',
'',
'',
'    EXECUTE IMMEDIATE req;',
'',
'    req := ''defaut UI'';',
'    -- Default UI',
'',
'    APEX_UI_DEFAULT_UPDATE.SYNCH_TABLE (',
'        p_table_name  => vname);',
'    COMMIT;    ',
'    for r in (select * from GFM_ITEMS where form_id = fid and tcomp = ''C'') loop',
'    req := r.code;',
'        APEX_UI_DEFAULT_UPDATE.UPD_COLUMN (',
'        p_table_name  => vname,',
'        p_column_name          => r.code,',
'        p_label                => r.lib,',
'        p_help_text            => r.description',
'        );',
'    end loop;',
'    COMMIT;',
'',
'',
'exception',
'when others then ',
'    --raise_application_error(-20300, :P11_ID||''-''||req );',
'    raise_application_error(-20300, vname||''.''||req ||''-''||sqlerrm);',
'end;',
